function convert_titlecase(sentence){
  let regex = /(^|\b(?!(and?|in?|at?|the|for|to|but|by|is)\b))\w+/g;
  //console.log(output); 
  return sentence.toLowerCase().replace(regex, s => s[0].toUpperCase() + s.slice(1));
}

